%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Análise de um MODEM digital%% %%  1 - banda do sinal modulado em AM%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;close all;clear all;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Análise da banda em frequência%% N    =  1;        % harmônicos%%% Expressão do Dn obtida na teoria e codifiqueiDn = @(A,tau,To,n) (A*tau/To)*(sinc(n*2*tau/(2*To))).*(1-exp(-j*n*tau*2*pi/To))%%% criar o nn     = [-N:1:N];%%% ConstantesA     = 1;      % amplitude da mensagemtau   = 10e-3;  % largura do pulso - simétricoTo    = 20e-3;  % períodowo    = 2*pi/To;% frequência angularfo    = 1/To;   % frequência em Hz%%% Cálculo de DnF_mt  = Dn(A,tau,To,n);%%% Visualizaçãofigure(1)stem(n*fo,abs(F_mt))set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%% PotênciaPot_mt = sum(abs(F_mt).^2);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - sintetizar a mensagem%% aux   = 0;Np    = 1000;tempo = linspace(0,To,Np);for m = 1:2*N+1    aux = aux + F_mt(m)*exp(j*wo*tempo);  endmt  = aux;%%% Visualizaçãofigure(2)plot(tempo,mt)set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 4 - sinal modulado - transmitido%% Ec        = 2;fc        = 1000;wc        = 2*pi*fc;portadora = cos(wc*tempo);   AM        = (Ec + mt).*portadora;%%% Visualizaçãofigure(3)plot(tempo,AM)set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);