%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% 0 - banda do sinal modulado em FM%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;close all;clear all;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Mensagem %% fc = 1000;      %%% frequência da portadoraTc = 1/fc;      %%% período da portadorawm = 1;         %%% frequência da mensagemTm = (2*pi);  %%% período da mensagemRes = Tc/100;   %%% resolução no tempoNp  = Tm/Res;   %%% número de pontos%%% vetor tempotempo = linspace(0,Tm,Np);%%% mensagemmt = 4*cos(tempo) + (4/3)*cos(3*tempo);%%% Visualizando a mensagemfigure(1)plot(tempo,mt)xlabel('Tempo')ylabel('Amplitude')set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%% O valor máximo da mensagemmp   = max(mt);%%% indíce de modulaçãoKf   = 20;        % constante de modulaçãoBm   = 3/(2*pi);  % Banda da mensagembeta = Kf*mp/Bm%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Sinal modulado %% FMt = cos(2000*pi*tempo + 160*pi*sin(tempo) + (160*pi/9)* sin(3*tempo));%%% Visualizando a mensagemfigure(2)plot(tempo,FMt)xlabel('Tempo')ylabel('Amplitude')set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - Análise em frequência %% F_FM      = fft(FMt);       % Fourier do FM%%% frequenciafs         = 1/Res;         % amostragemfrequencia = linspace(-fs/2,fs/2,Np);%%% visualizaçãofigure(3)plot(frequencia,fftshift(abs(F_FM)))xlabel('Tempo')ylabel('Amplitude')set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%% Cáculo teórico da banda - estimativaBanda = 2*(beta+1)*Bm