%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% 0 - Sistemas lineares%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;close all;clear all;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% Ganho em omega - rd/sGanho   =  @(w) 10./((j*w+1).*(j*w+10));%%% caracterizando a variável tempow     = 1;                    %%% frequência angular - cos(t)f     = w/(2*pi);             %%% frequenciaT     = 1/f;                  %%% períodoNp    = 1000;                 %%% resolução gráficatempo = linspace(0,T,Np);%%% crio a entradaxt = cos(tempo);%%% GanhoHw = Ganho(w);%%% saíday2 = abs(Hw)*cos(tempo+angle(Hw));%%% Visualizaçãofigure(1)plot(tempo,xt); hold;plot(tempo,y2);xlabel('Tempo')ylabel('Amplitude')set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% No domínio da frequênciaomega  = linspace(0,10,Np);Filtro = Ganho(omega);figure(2)subplot(2,1,1); plot(omega,(abs(Filtro))); set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);subplot(2,1,2); plot(omega,angle(Filtro)*180/pi); set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);