%% Sistemas  Linearesclear all;clc;close all;%% definindo o númerode harmônicas envolvidas na análise / sinteseN                = 10;                      % Número de harmônicas para análise%% Definindo o sinal a ser estudado%% Ona pulsada de dois níveis, período To e largura tau  sinal.tipo       ="quadrada"  sinal.Ap         = 1;                       % Amplitude em nível alto  sinal.An         = 0;                       % Amplitude em nível baixo  sinal.largura    = 0.5;                     % Duração em nível alto  sinal.periodo    = 1;                       % Período  sinal.harmonicas = N;                       % Número de harmônicas%%% Definindo o vetor de sintese  sintese.pontos   = 1000;  sintese.periodos = 1;  sintese.periodo  = sinal.periodo;  sintese.Dn       = Dn;  sintese.N        = sinal.harmonicas;%% Analisando o sinal - s(t) -%% Tempo para frequência  [D_o,Dn,f,Pg,PN] = sFourier(sinal);  disp("Valor Médio:"); D_o  disp("Potência:"); Pg  disp("Potência (N):"); PN(end) %% Sintetizando o sinal%% Frequência para tempo  [tempo,sinal] = sinteseFourier (sintese);    %%%%%%%%% Visualização  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  %%% Visualizando os espectrosfigure(1) stem(f,abs(Dn)); title('Série de Fourier do sinal g(t)');xlabel('Frequência em Hz');ylabel('Amplitude em  volts')  %%% Visualizando o sinal reconstruidofigure(2)plot(tempo,sinal)title('Reconstrução do sinal g(t)');xlabel('Tempo em segundos');ylabel('Amplitude em  volts')%%% Visualizando a pot6encia em função da frequênciafigure(3)plot(f(N+1:end),PN,f(N+1:end),ones(1,N+1)*Pg)title('Potência total e em função do harmônico');xlabel('Harmônico');ylabel('Potência em W')  
