%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;                        % apaga todas as variáveisclose all;                        % fechar as figurasclc;                              % limpa o ambiente de simulaçãopkg load symbolic                 % somente para o OctaveN = 7;                           % número de harmôncias para análise%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Definir o sinal g(t) - onda pulsadaA1  = 2;                           % Nível alto da onda pulsadaA2  = 0;                           % Nível baixo da onda pulsadatau = 1.7;                        % Largura o pulso em nível altoT0  = 6.25;                           % Periodo do sinal  w0  = 2*pi/T0;                     % frequência angularf0  = 1/T0;                        % frequência angularn   = [-N:1:N];                    % harmônicas de Fourierf   = n*f0;                        % frequência das harmônicas%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Calcular a integral de Fourier : tempo em frequênciasyms t nDn  = inv(T0)*int(A1*exp(-j*n*w0*t),t,0,tau) + inv(T0)*int(A2*exp(-j*n*w0*t),t,tau,T0);D0  = inv(T0)*int(A1,t,0,tau) + inv(T0)*int(A2,t,tau,T0);Dnaux = Dn;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - Calcular os valores numéricos de Dnn   = [-N:1:N];                     % redefine os valores de nDn  = eval(Dn);                     % valores numéricosD0  = eval(D0);                     % valor médioDn(N+1) = D0;                       % substitui o valor correto de D0%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 4 - Sintese do sinal g(t) --> a partir de Dn, somar as projeções %%  4.1 - Definir o tempoM    = 1000;                        % número de pontos de simulaçãoTs   = T0/M;                        % passo de simulaçãotempo= [0:Ts:T0];                   % vetor tempo%%  4.2 - Somatória do sinalsomatoria = 0;                      % incializa a somatóriafor k = 0:2*N                       % 2*N + 1 --> pontos    somatoria = somatoria + Dn(k+1)*exp(j*n(k+1)*w0*tempo);  endgt   =  somatoria;                  % sinal sintetizado%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 5 - Visualizar: Dn (análise) e g(t) (síntese) figure(1)                           % domínio da frequênciastem(f,abs(Dn));xlabel('Frequência em Hertz')ylabel('Amplitude em Volts')title('Espectro de Amplitudes')figure(2)                           % domínio do tempoplot(tempo,gt);xlabel('Tempo em segundos')ylabel('Amplitude em Volts')title('Sinal no tempo')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 6 - Cáculo de potência do sinal%%  6.1 - Cáculo no tempoPg  =  inv(T0)* int(A1^2,t,0,tau) + inv(T0)* int(A2^2,t,tau,T0);Pg  =  eval(Pg);%%  6.2 - Cáculo na frequênciaVetor = [abs(Dn(N+1)^2) 2*abs(Dn(N+2:end)).^2];Pgn   = cumsum(Vetor);clc;Banda = (Pgn(end)/Pg)*100;