//%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%//%% 1 - Preparação do código //%% //%% Boas práticas: limpeza de variáveis; variáveis globais//%% Constantes; carregar bibliotecas;...//%%clear all;          //% limpa as variáveis// close all;          //% fecha todas as figuras ativasclc;                //% limpa a tela visível//%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%//%% 2 - Sinais //%% //%% Análise de um sinal g(k) - sinal amostrado//%% //%% fs = 11025;                         //% frequência de amostragemfmax    = fs/2;                     //% frequência máxima pelo teorema da amostragemM       = 10;                       //% um sinal cuja frequência é 10 vezes menorfsinal  = fmax/M;                   //% a frequência do sinal é uma fraça da máximacontinuo = ones(1,100);                 //% nível continuo artificialt        = linspace(0,1/fsinal,10*M);   //% o vetor temposenoide  =sin(2*%pi*fsinal*t);           //% crio o sinal senoidalsenoide2  =sin(4*%pi*fsinal*t);          //% crio o sinal senoidalgk = continuo + senoide + senoide2;     //% sinal continuo com 4 amostras//%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%//%% 3 - Pré processamento do sinal//%% //%% extrair informações importantes do sinal//%%N       = length(gk);           //% determina o tamanho do vetorTs      = 1/fs;                 //% taxa de amostragem no tempoduracao = N*Ts;                 //% duração do sinal no tempotempo   = linspace(0,N*Ts,N);   //% vetor tempo//%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%//%% 4 - Determinar a série discreta de fourier//%% //%% X[n] = 1/N * sum_{k=0}^{N-1} gk exp(-j*n*k*2*pi/n)tic;for n = 0 : N-1                    //% variando os valores de n  aux_k = 0;                         //% valor inicial da variável  for k = 0 : N-1                  //% N pontos - 0 --> N-1      aux_k = aux_k + gk(k+1)*exp(-%i*n*k*2*%pi/N);  //% acumula a somatória    end  X(n+1) = aux_k/N;                             //% atualizo o valor final  endtoc                                             //% medição de tempo//%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%//%% 5 - Visualizando//%% //%%  frequência e tempo//%%figure(1)plot(fftshift(abs(X)),'linewidth', 3)         //% plot(x,y,azul com linha cheia)xlabel('Frequência em Hz')                    //% eixo xylabel('Amplitude em volts')                  //% eixo ytitle('Análise de Fourier')                   //% títulofigure(2)plot(tempo,gk,'b-','linewidth', 3)   //% plot(x,y,azul com linha cheia)xlabel('Tempo em segundos')          //% eixo xylabel('Amplitude em volts')         //% eixo ytitle('Sinal amostrado')             //% título