%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;            % limpa as variáveisclose all;            % fecha todas as figurasclc;                  % limpa a tela que está sendo visualizada% https://studio.youtube.com/video/umBm2TOLi20/edit%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Gerar um som harmônico%% %% g(t) --> som = Ap * cos(w*tempo+fase)%%%%Ap   = 1;               % amplitude(volume) do somf    = 440;             % frequência do som em Hz(Lá)w    = 2*pi*f;          % determino a frequência angularfase = 0;               % fase do sinal harmônico%%% criar o conceito de tempoduracao    = 1;         % duração do som de 1 segundoamostragem = 11025;     % frequência de uso do computador  tempo      = linspace(0,duracao,amostragem);      %%% gerar o sinalgt   = Ap*cos(w*tempo+fase);figure()plot(tempo,gt);grid;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - reproduzir o som que eu criei%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - Criar sons novosgt1 = gt;gt2 = gt1 + Ap/3*cos(3*w*tempo+fase);gt3 = gt2 + Ap/5*cos(5*w*tempo+fase);sound(gt,amostragem);figure()plot(tempo,gt);grid;