%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% 0 - Orientações para o trabalho%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;close all;clear all;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% 2 - Anaálise no dominio do tempo%%% recuperar o sinal a partir de um arquivo - real[gk,fs] = audioread ('a.wav');       % transformei um arquivo .wav em um vetor g(k)                                     % recuperei a taxa de amostragem - fs%%% tempoTs      = 1/fs;                          % resoluÇão temporalNg      = length(gk);                    % Tamanho do vetor de dadosTfinal  = Ng*Ts;                         % tempo finaltempo   = linspace(0,Tfinal,Ng);         % vetor tempo%%% Visualizaçãofigure(1)  plot(tempo,gk); grid;xlabel('Tempo')ylabel('Amplitude')set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% %% %% 3 - Trabalhando no domínio da frequênciaGK    = fft(gk);                   % comando nativo do Octavefreq  = linspace(-fs/2,fs/2,Ng);   % vetor frequência %%% Módulo de GKGK_mod = abs(GK);%%% ajuste para centralizar o zero - espectro bilateralGK_mod = fftshift(GK_mod);%%% acertar a fase e visualizar em grausGK_fase = angle(GK)*180/pi;%%% Montando os gráficosfigure(2) subplot(2,1,1);stem(freq,GK_mod);xlabel('Frequencia em Hz');ylabel('Modulo');title('Transformada de Fourier do sinal');  grid;%%% Melhora a aparênciaset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2); subplot(2,1,2);stem(freq,GK_fase);xlabel('Frequencia em Hz');ylabel('Fase em graus');  title('Transformada de Fourier do sinal') ;grid; %%% Melhora a aparênciaset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);   %%% Montando os gráficosGK_mod_dB = 20*log10(GK_mod/max(GK_mod));figure(3) subplot(2,1,1);stem(freq,GK_mod_dB);xlabel('Frequencia em Hz');ylabel('Modulo');title('Transformada de Fourier do sinal');  grid;%%% Melhora a aparênciaset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2); subplot(2,1,2);stem(freq,GK_fase);xlabel('Frequencia em Hz');ylabel('Fase em graus');  title('Transformada de Fourier do sinal') ;grid; %%% Melhora a aparênciaset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);  %%% Visualziando somente o módulofigure(4) plot(freq,GK_mod_dB);xlabel('Frequencia em Hz');ylabel('Modulo');title('Transformada de Fourier do sinal');  grid;                           