%% Determinando o termo Dn simbolicamentesyms n tDn = inv(To)*int(A1*exp(-j*n*wo*t),t,0,tau) + inv(To)*int(A2*exp(-j*n*wo*t),t,tau,To);D_o = inv(To)*int(A1,t,0,tau) + inv(To)*int(A2,t,tau,To);%% Determinando o termo Dn numericamenten=[-N:1:N]; Dn = eval(Dn)D_o = eval(D_o)      % Corrigindo o valor médio (NaN devido a indeterminação)Dn(N+1) = D_o        % Subistituindo no vetor de respostas