%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  %% Elaborar um código que:%%%% 1. leia um arquivo de áudio - tipo wav%% 2. Mostrar em um gráfico o sinal lido%% 3. Explorar as características do sinal%%    máximo, mínimo, desvio padrão, média,...%% 4. Processar o sinal --> aplicar algum algoritmo --> filtro%% 5. Visualizar o resultado do processamento%%%% Autor: VCP - Data: 11/08/2021 - Versão 0.0%%%% https://octave.sourceforge.io/packages.php%%%% https://github.com/vparro%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  1 - Boas práticas%%   display('Executando a parte 1...') clear all;              % Limpa todas as variáveisclose all;              % fecha todas as figurasclc;                    % limpa a tela display('Parte 1 executada')%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  2 -  Entrada de dados%% %%  Leitura dos dados%%  Extrai informações do arquivo%%  Reproduz o sinal em áudio%%display('Executando a parte 2...')info    = audioinfo ('Gaita_Blues.wav')';      % Informações do arquivo [gk,fs] = audioread ('Gaita_Blues.wav');       % leitura do arquivo % gk   --> os valores do sinal de áudio% fs   --> taxa de amostragem em Hz% info --> informações do  arquivo de áudio%%%%%%% Ouvir o som no computador% sound(gk,fs)   % colocar um comentário para evitar de executar o comandodisplay('Parte 2 executada') %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  3 -  Análise do sinal de áudio%% %%  Explorar as características do sinal%%  máximo, mínimo, desvio padrão, média,...%%g_max   = max(gk);          % valor máximog_min   = min(gk);          % valor mínimog_medio = mean(gk);         % médiag_std   = sqrt(var(gk));    % desvio padrão%%%%%%% Graficamente o sinalAmostras = info.TotalSamplesDuracao  = info.Duration%%%%%%% Cria o vetor tempotempo    = linspace(0,Duracao,Amostras);        figure(1)                   % cria a figura 1%%%%%%% cria o gráfico de gk em função do tempoplot(tempo,gk, 'k-.');             %%%%%%% identifica os eixosxlabel('Tempo em segundos')ylabel('Amplitude Normalizada')title('Serie temporal de uma gaita blues')%%%%%%% Cria o reticuladogrid minor  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  4 -  Processamento de sinais%% %%  Média móvel de N amostras%%%%%%%%% N amostras do média móvelN    = 100;   for n = 1:(Amostras - N)  %%%%%%% saída do filtro média móvel    yk(n) = mean(gk(n:n+N));  endfigure(2)                   % cria a figura 1%%%%%%% cria o gráfico de gk em função do tempoplot(yk, 'k-.');             %%%%%%% identifica os eixosxlabel('Tempo em segundos')ylabel('Amplitude Normalizada')title('Serie temporal da media movel de uma gaita blues')%%%%%%% Cria o reticuladogrid minor 