%%% Incia o ambiente matlab para uma sessão de análisesclear; clc; close all;pkg load signal%%% Carrega o trabalho feito anteriormente e gravado no arquivo Voz.matload('Voz.mat') % Cria uma figura para a análise temporalplot(t,Vp);title('Sinal de voz para a vogal a');xlabel('Tempo em s');ylabel('Tensão em Volts'); grid minor%%% modifica parâmetros do gráficoset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);% Cria uma figura para a análise em frequênciaplot(frequencia,Y0);title('Espectro de amplitude da vogal a');xlabel('Frequência em Hz');ylabel('Magnitude em dB'); grid minorset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%% Analisando os parâmteros graficamenteti = 0.001126;  % começo do primeiro período inteirotf = 0.09537;   % fim do último período inteiroT = (tf-ti)/13; % valor médio dentre os 13 períodosf = inv(T);     % frequência associada a vibração geradafindpeaks(abs(Vp),'MinPeakDistance',0.001);[vt,tp]=findpeaks(Vp,t,"MinPeakDistance",0.001);Tm =mean(diff(tp));title('Sinal de voz para a vogal a');xlabel('Tempo em s');ylabel('Tensão em Volts'); grid minor