%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% a.1 Resposta de um sistema de segunda ordem %% %%  N(s) = a%%  D(s) = s^2 + bs + c%%%% https://octave.sourceforge.io/optim/function/expfit.htmlN     = 1;          % numeradorb     = 1;          % ?: atenuaçãoc     = 10;         % ?: oscilaçãoD     = [1 b c];    % denominadorGs    = tf(N,D);    % função de transferência estimada%%% visualizarfigure(2)c = [0:1:10];for k = 1 : length(c)  D     = [1 b c(k)];    % denominador  Gs    = tf(N,D);    % função de transferência estimada    impulse(Gs);  end%%% modifica parâmetros do gráficoset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%% Resposta do sistema no domínio do tempofigure(3)[serie,tempo,X] = impulse(Gs);impulse(Gs)%%% Modifica parâmetros do gráficoset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%% Criando artificialmente a vogalserie            = serie';vogal_artificial = [serie serie serie serie serie serie serie];vogal_artificial = [vogal_artificial vogal_artificial];plot(vogal_artificial)%%% Modifica parâmetros do gráficoset(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% b. Plot sobreposto da vogal com a resposta de Laplace. (2.0 pontos) %% %%  %%%%  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% c. Qual a relação entre Laplace(polos) e Fourier? (1.0 ponto)%%%% %%%%  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%  d. s = sigma + j w --> G(s) --> visualizar o gráfico do ponto de vista de w. %%     Comparando com Fourier qual a conslusão? (1.0 ponto)%% %% %%%%  %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% e. Gerar uma vogal artificial (sintese de voz) de 2 segundos %%    - o mais realista possível (1.0 ponto).%% %% %%%%  %%%%%%%%%%%%%%%