%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclear all;clc;close all;pkg load control;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%   1 -  equação do ganho do percursoalpha   = 0.2;          % atenuação do percursof       = 2.45e9;     % frequência do sinal em GHzw       = 2*pi*f;     % frequ6encia angular do sinalvp      = 3e8;        % velocidade de propagação em espaço livre (luz) - m/s%%% Variação de percursoNp      = 100;                                 % número de pontosdelta_x = linspace(0,20000, Np);  delta_t = delta_x/vp;                           % variação de tempo%%% wdelta_twdelta_t = w*delta_t;                           % argumento da função    Ganho_p = sqrt((1 + alpha*cos(wdelta_t)).^2 + (alpha*sin(wdelta_t)).^2);%%% Visualizarplot(delta_x, Ganho_p)set(findall(gcf,'Type','line'),'LineWidth',3);set(gca,'FontSize',14,'LineWidth',2);