%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Boas práticasclc;clear all;close all;%%% carregando o pacote de controle e sinaispkg load control;pkg load signal;%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Sinal: calibração e sinal real%%%% trabalhar com um sinal amostrado%% %% trabalhar com um sinal real - gaita - hamônica [gk,fs] = audioread ('gaita.wav'); %% O arquivo foi criado com fs = 44.100 Hz%% f_max (Gaita) = 44.100Hz/2 = 22.050Hz (áudio)%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - Sinal: estudo do espectro de energia do sinal%%%%% Mostra o Fourier completoFourier_Grafico (gk, fs, 1);%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% Perguntas%%%%% Qual a taxa de amostragem limite?%%%%% Qual a faixa de frequência que contém 95% da energia?%%%%% Como reduzir o número de amostras do sinal?%%  [y, h] = resample (x, p, q)%%% Reduzindo a amostragem em 10 vezesFr = 8;%%% Nova amostragemfs = fs/Fr;[Y, H] = resample (gk, 1, Fr);Nf =2;%%% Mostra o Fourier reduzidoFourier_Grafico (Y, fs, Nf);      