%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 0 - Sintese de uma série temporal a partir de G(z) %% %%  Z --> tempo %%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 1 - Preparação do código %% %% Boas práticas: limpeza de variáveis; variáveis globais%% Constantes; carregar bibliotecas;...%%%%% Limpezaclc;          % limpa visual da tela de comandosclose all;    % limpa as figurasclear all;    % limpa as variáveis%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 2 - Função em Z%% %%%%%  G(z) --> Numerador   = z * sin(wT)%%%  G(z) --> Denominador = z^2 - 2z * cos(wT) + 1%%% Amostragem - TT    = 1;            % amostragem de 1 segundof    = 1/T;          % frequência de amostragemfmax = f/2;          % frequência máxima que pode ser                      % amostrada pro este sistema                      %%% Escolha da frequência wfreq = fmax/10;      % escolha arbitráriaw    = 2*pi*freq;    % frequência angularwT   = w*T;          % determino o parâmetro wT%%% Recalculo o numerador e denominadorn1   = sin(wT);      % parâmetro do numeradord1   = cos(wT);      % parâmetro do denominador%%%  G(z) --> Numerador   = z * 0.30902%%%  G(z) --> Denominador = z^2 - 2z * 0.95106 + 1%%%  G(z) --> Denominador = z^2 - z * 1.9021 + 1%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%% 3 - Simulador da série temporal%% %%%%%  G(z) --> Denominador = z^2 - 2z * 0.95106 + 1%%%  G(z) --> Denominador = z^2 - z * 1.9021 + 1%%% Preditor - simulador%%%%%% y(k) = n1 x(k-1) +2 d1 y(k-1) - y(k-2)%%% Entrada e número de pontos de simulação%%% Entrada do tipo impulsoNx    = 100;              % número de pontos de simulaçãoxk    = [1 zeros(1,Nx)];  % impulso unitário%%% Interpretar as variáveis:%%% y(k)           - presente%%% x(k-1) = xa    - passado%%% y(k-1) = ya    - passado%%% y(k-2) = y2a   - passado%%%% k = 0 --> y(0) = n1 x(0-1) +2 d1 y(0-1) - y(0-2)%%%% k = 1 --> y(1) = n1 x(1-1) +2 d1 y(1-1) - y(1-2)%%%% k = 2 --> y(2) = n1 x(2-1) +2 d1 y(2-1) - y(2-2)%%%% k = 3 --> y(3) = n1 x(3-1) +2 d1 y(3-1) - y(3-2)%%% Valores inciais para as variáveisxa    = 0;    % valor no passadoya    = 0;y2a   = 0;for m = 0 : Nx-2    % tempo    k    = m + 1;     % indice Octave     %%% cálculo do presente    y(k) = n1*xa + 2*d1*ya - y2a;    %%% atualizei as variáveis  %%% armazenar o passado    xa   = xk(k);  y2a  = ya;  ya   = y(k);     %%% 7 posições de memória --> 100 pontos no tempo !!!!  end%%% Visualizar o resultado da série temporalstem(y)